#uses &quot;OpenLibrary/Constants/Colors&quot;
#uses &quot;OpenLibrary/Classes/Components/Component&quot;
#uses &quot;OpenLibrary/Classes/Implementations/ActionBehaviors/SetDpeValue&quot;
#uses &quot;OpenLibrary/Classes/Implementations/DisplayBehaviors/SpecialCase/DynamicEnableWithColor&quot;
#uses &quot;OpenLibrary/Classes/Implementations/DisplayBehaviors/Common/DynamicEnable&quot;
#uses &quot;OpenLibrary/Macros&quot;

string dp = $dpe;
const float RESCALE_TREND_MAX_BUFFER = 1.10;  //Provides a buffer gap between max trend value and top of graph
private float historicalTrendMax;

void initializePanel()
{
  resizePanelToShape(getShape(&quot;border&quot;));
  titleBar(false);

  dyn_string bannerText = makeDynString(
      &quot;Status OK&quot;,
      &quot;Input Out of Range&quot;,
      &quot;Input PER Invalid&quot;,
      &quot;Value Oscillation Failed&quot;,
      &quot;Process Value Close to Set Point&quot;,
      &quot;SP Changed During Tuning&quot;,
      &quot;Pre Tuning During Fine Tuning&quot;,
      &quot;Invalid Output Value Limits&quot;,
      &quot;Invalid Fine Tuning Parameter&quot;,
      &quot;Input Invalid Format&quot;,
      &quot;Output Calculation Error&quot;,
      &quot;Sampling Timer Error&quot;,
      &quot;Set Point Invalid Format&quot;,
      &quot;Manual Invalid Format&quot;,
      &quot;Substitute Output Invalid Format&quot;,
      &quot;Disturbance Invalid Format&quot;,
      &quot;E-Stop&quot;);
  dyn_string bannerColors = makeDynString(
      OK_STATUS_COLOR,
      ERROR_STATUS_COLOR,
      ERROR_STATUS_COLOR,
      ERROR_STATUS_COLOR,
      ERROR_STATUS_COLOR,
      ERROR_STATUS_COLOR,
      ERROR_STATUS_COLOR,
      ERROR_STATUS_COLOR,
      ERROR_STATUS_COLOR,
      ERROR_STATUS_COLOR,
      ERROR_STATUS_COLOR,
      ERROR_STATUS_COLOR,
      ERROR_STATUS_COLOR,
      ERROR_STATUS_COLOR,
      ERROR_STATUS_COLOR,
      ERROR_STATUS_COLOR,
      ERROR_STATUS_COLOR);
  dyn_string bannerTextColors = generateContrastingColors(bannerColors, LIGHT_TEXT_COLOR, DARK_TEXT_COLOR);

  errorBanner.setParameters(bannerText, bannerColors, bannerTextColors);


  // Read/write textbox permissions

  estopValueTextField.addDynamicEnableBehavior(dp + &quot;.iEstopFunction&quot;, 2);
  proportionalTextField.addDynamicEnableBehavior(dp + &quot;.bTOG_ManualTuning&quot;);
  integralTextField.addDynamicEnableBehavior(dp + &quot;.bTOG_ManualTuning&quot;);
  derivativeTextField.addDynamicEnableBehavior(dp + &quot;.bTOG_ManualTuning&quot;);
  proportionalWeightTextField.addDynamicEnableBehavior(dp + &quot;.bTOG_ManualTuning&quot;);
  derivativeWeightTextField.addDynamicEnableBehavior(dp + &quot;.bTOG_ManualTuning&quot;);


  // Panel-specific buttons

  pidAutoButton.addActionBehavior(new SetDpeValueActionBehaviorClass(dp + &quot;.bPB_AutomaticMode&quot;));
  pidAutoButton.addDisplayBehavior(new StaticTextDisplayBehaviorClass(&quot;PID Auto&quot;));
  pidAutoButton.addDisplayBehavior(new DynamicEnableWithColorDispayBehaviorClass(dp + &quot;.bPBEN_AutomaticMode&quot;, dp + &quot;.iPIDMode&quot;, BUTTON_ACTIVE_COLOR, BUTTON_NEUTRAL_COLOR), 3);

  pidManualButton.addActionBehavior(new SetDpeValueActionBehaviorClass(dp + &quot;.bPB_ManualMode&quot;));
  pidManualButton.addDisplayBehavior(new StaticTextDisplayBehaviorClass(&quot;PID Manual&quot;));
  pidManualButton.addDisplayBehavior(new DynamicEnableWithColorDispayBehaviorClass(dp + &quot;.bPBEN_ManualMode&quot;, dp + &quot;.iPIDMode&quot;, BUTTON_ACTIVE_COLOR, BUTTON_NEUTRAL_COLOR), 4);

  preTuneButton.addActionBehavior(new SetDpeValueActionBehaviorClass(dp + &quot;.bPB_Pretune&quot;));
  preTuneButton.addDisplayBehavior(new StaticTextDisplayBehaviorClass(&quot;Pre Tune&quot;));
  preTuneButton.addDisplayBehavior(new DynamicEnableWithColorDispayBehaviorClass(dp + &quot;.bPBEN_Pretune&quot;, dp + &quot;.iPIDMode&quot;, BUTTON_ACTIVE_COLOR, BUTTON_NEUTRAL_COLOR), 1);

  fineTuneButton.addActionBehavior(new SetDpeValueActionBehaviorClass(dp + &quot;.bPB_FineTune&quot;));
  fineTuneButton.addDisplayBehavior(new StaticTextDisplayBehaviorClass(&quot;Fine Tune&quot;));
  fineTuneButton.addDisplayBehavior(new DynamicEnableWithColorDispayBehaviorClass(dp + &quot;.bPBEN_FineTune&quot;, dp + &quot;.iPIDMode&quot;, BUTTON_ACTIVE_COLOR, BUTTON_NEUTRAL_COLOR), 2);

  manualTuneButton.addActionBehavior(new SetDpeValueActionBehaviorClass(dp + &quot;.bPB_ManualTuning&quot;));
  manualTuneButton.addDisplayBehavior(new StaticTextDisplayBehaviorClass(&quot;Manual Tune&quot;));
  manualTuneButton.addDisplayBehavior(new DynamicColorDisplayBehaviorClass(dp + &quot;.bTOG_ManualTuning&quot;, BUTTON_ACTIVE_COLOR, BUTTON_NEUTRAL_COLOR));


  //======================= Trend box scaling &amp; curve initialization =========================//
  //Scale trendline on value change
  dpConnect(&quot;onPVchange&quot;, dp + &quot;.rActualInput&quot;, dp + &quot;.rOutput&quot;, dp + &quot;.rManualSP&quot;, dp + &quot;.rAutoSP&quot;);

  //Change setpoint displayed on trendline (Auto vs. Manual)
  dpConnect(&quot;onModeChange&quot;, dp + &quot;.iMode&quot;);

  //Set trend curve colors
  Trend.curveColor(&quot;curveOutput&quot;, TREND_COLOR_1);
  Trend.curveColor(&quot;curveInput&quot;, TREND_COLOR_2);
  Trend.curveColor(&quot;curveAutoSP&quot;, TREND_COLOR_3);
  Trend.curveColor(&quot;curveManualSP&quot;, TREND_COLOR_3);
}

void onPVchange(string processValueDpe, float processValue, string outputDpe, float output, string autoSetpointDpe, float autoSetpoint, string manualSetpointDpe, float manualSetpoint)
{
  //set maxValue to largest of the processValue, output, manualSetpoint, or autoSetpoint
  float maxValue = dynMax(makeDynFloat(processValue, output, manualSetpoint, autoSetpoint));

  //compare historical max to current max and set historical max to highest value between the two
  historicalTrendMax = maxValue &gt; historicalTrendMax ? maxValue : historicalTrendMax;

  //Set new scale for all curves based on historical max
  float newScaleMax = historicalTrendMax*RESCALE_TREND_MAX_BUFFER;

  Trend.curveMinMax(&quot;curveAutoSP&quot;, 0, newScaleMax);
  Trend.curveMinMax(&quot;curveManualSP&quot;, 0, newScaleMax);
  Trend.curveMinMax(&quot;curveInput&quot;, 0, newScaleMax);
  Trend.curveMinMax(&quot;curveOutput&quot;, 0, newScaleMax);
}

void onModeChange(string dpe, int mode)
{
  //Show or hide auto SP curve + legend
  Trend.curveVisible(&quot;curveAutoSP&quot;, (mode == 1) ? TRUE : FALSE);
  Trend.curveLegendVisibility(&quot;curveAutoSP&quot;, (mode == 1) ? TRUE : FALSE);

  //Show or hide manual SP curve + legend
  Trend.curveVisible(&quot;curveManualSP&quot;, (mode == 1)? FALSE : TRUE);
  Trend.curveLegendVisibility(&quot;curveManualSP&quot;, (mode == 1) ? FALSE : TRUE);
}
